@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Brand Color Scheme: White (#ffffff), Green (#28a745), Black (#000000) */
  --background: hsl(0, 0%, 100%); /* White */
  --foreground: hsl(0, 0%, 0%); /* Black */
  --muted: hsl(210, 40%, 95%); /* Light gray */
  --muted-foreground: hsl(0, 0%, 45%); /* Dark gray */
  --popover: hsl(0, 0%, 100%); /* White */
  --popover-foreground: hsl(0, 0%, 0%); /* Black */
  --card: hsl(0, 0%, 100%); /* White */
  --card-foreground: hsl(0, 0%, 0%); /* Black */
  --border: hsl(220, 13%, 91%); /* Light gray border */
  --input: hsl(220, 13%, 91%); /* Light gray input */
  --primary: hsl(134, 61%, 41%); /* Green #28a745 */
  --primary-foreground: hsl(0, 0%, 100%); /* White on green */
  --secondary: hsl(210, 16%, 98%); /* Very light gray */
  --secondary-foreground: hsl(0, 0%, 0%); /* Black */
  --accent: hsl(134, 61%, 41%); /* Green #28a745 */
  --accent-foreground: hsl(0, 0%, 100%); /* White on green */
  --destructive: hsl(0, 84%, 60%); /* Red for errors */
  --destructive-foreground: hsl(0, 0%, 100%); /* White */
  --ring: hsl(134, 61%, 41%); /* Green focus ring */
  --radius: 0.5rem;

  /* Brand colors for direct use */
  --brand-primary: hsl(0, 0%, 100%); /* White */
  --brand-accent: hsl(134, 61%, 41%); /* Green #28a745 */
  --brand-accent-dark: hsl(134, 61%, 31%); /* Darker green #1e7e34 */
  --brand-accent-light: hsl(134, 61%, 51%); /* Lighter green #34ce57 */
  --brand-text: hsl(0, 0%, 0%); /* Black */
}

.dark {
  /* Dark mode: Black background, White text, same Green accent */
  --background: hsl(0, 0%, 0%); /* Black */
  --foreground: hsl(0, 0%, 100%); /* White */
  --muted: hsl(0, 0%, 15%); /* Dark gray */
  --muted-foreground: hsl(0, 0%, 65%); /* Light gray */
  --popover: hsl(0, 0%, 5%); /* Very dark gray */
  --popover-foreground: hsl(0, 0%, 100%); /* White */
  --card: hsl(0, 0%, 5%); /* Very dark gray */
  --card-foreground: hsl(0, 0%, 100%); /* White */
  --border: hsl(0, 0%, 15%); /* Dark gray border */
  --input: hsl(0, 0%, 15%); /* Dark gray input */
  --primary: hsl(134, 61%, 41%); /* Same green #28a745 */
  --primary-foreground: hsl(0, 0%, 100%); /* White */
  --secondary: hsl(0, 0%, 10%); /* Very dark gray */
  --secondary-foreground: hsl(0, 0%, 100%); /* White */
  --accent: hsl(134, 61%, 41%); /* Same green #28a745 */
  --accent-foreground: hsl(0, 0%, 100%); /* White */
  --destructive: hsl(0, 62%, 30%); /* Darker red */
  --destructive-foreground: hsl(0, 0%, 100%); /* White */
  --ring: hsl(134, 61%, 41%); /* Green focus ring */
  --radius: 0.5rem;

  /* Brand colors for dark mode */
  --brand-primary: hsl(0, 0%, 0%); /* Black */
  --brand-accent: hsl(134, 61%, 41%); /* Same green */
  --brand-accent-dark: hsl(134, 61%, 31%); /* Darker green */
  --brand-accent-light: hsl(134, 61%, 51%); /* Lighter green */
  --brand-text: hsl(0, 0%, 100%); /* White */
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-white text-black;
  }

  /* Brand-consistent typography */
  h1, h2, h3, h4, h5, h6 {
    @apply text-black font-bold;
  }

  /* Link styles with brand colors */
  a {
    @apply text-black hover:text-brand-accent transition-colors duration-200;
  }

  /* Brand button styles */
  .btn-brand-primary {
    @apply bg-brand-accent text-white hover:bg-brand-accent-dark transition-colors duration-200 px-6 py-2 rounded font-medium;
  }

  .btn-brand-secondary {
    @apply bg-white text-black border-2 border-black hover:bg-gray-50 transition-colors duration-200 px-6 py-2 rounded font-medium;
  }
}

@layer utilities {
  /* Brand color utilities */
  .text-brand-primary {
    color: var(--brand-primary);
  }

  .text-brand-accent {
    color: var(--brand-accent);
  }

  .text-brand-text {
    color: var(--brand-text);
  }

  .bg-brand-primary {
    background-color: var(--brand-primary);
  }

  .bg-brand-accent {
    background-color: var(--brand-accent);
  }

  .bg-brand-accent-dark {
    background-color: var(--brand-accent-dark);
  }

  .bg-brand-accent-light {
    background-color: var(--brand-accent-light);
  }

  .border-brand-accent {
    border-color: var(--brand-accent);
  }

  .border-brand-text {
    border-color: var(--brand-text);
  }

  /* Hover states */
  .hover\:bg-brand-accent-dark:hover {
    background-color: var(--brand-accent-dark);
  }

  .hover\:text-brand-accent:hover {
    color: var(--brand-accent);
  }

  /* Focus states */
  .focus\:ring-brand-accent:focus {
    --tw-ring-color: var(--brand-accent);
  }

  /* Marquee Animation */
  @keyframes marquee {
    0% {
      transform: translateX(0%);
    }
    100% {
      transform: translateX(-50%);
    }
  }

  .animate-marquee {
    animation: marquee 30s linear infinite;
  }

  .animate-marquee-slow {
    animation: marquee 45s linear infinite;
  }

  /* Line clamp utility for testimonials */
  .line-clamp-4 {
    display: -webkit-box;
    -webkit-line-clamp: 4;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
}

